-- drop the database if it already exists
drop database if exists eventnova;

-- create the database
create database eventnova;

-- use the database
use eventnova;

-- guests table
create table guests (
    guest_id int not null auto_increment primary key,
    gname varchar(100) not null,
    gemail varchar(100) not null,
    gpassword varchar(100) not null,
    gphone varchar(10) null,
    gusername varchar(100) not null,
    glocation varchar(100) not null -- Updated to match model
);

-- organizers table
create table organizers (
    organizer_id int not null auto_increment primary key,
    oname varchar(100) not null,
    oemail varchar(100) not null,
    opassword varchar(100) not null,
    ophone varchar(15) not null,
    odescription text null,
    ousername varchar(100) not null
);

-- events table
CREATE TABLE events (
    event_id INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    organizer_id INT NOT NULL,
    event_name VARCHAR(100) NOT NULL,
    event_thumbnail VARCHAR(500) NOT NULL,
    event_type VARCHAR(50) NOT NULL,
    genre VARCHAR(150) NOT NULL, -- Updated to match model
    date DATE NOT NULL,
    time TIME NOT NULL,
    venue VARCHAR(100) NOT NULL,
    city VARCHAR(15) NOT NULL,
    price DECIMAL(10, 2) NOT NULL,
    available_seats INT NOT NULL,
    event_description TEXT NOT NULL, -- Changed to NOT NULL to match model
    FOREIGN KEY (organizer_id) REFERENCES organizers(organizer_id)
);

-- bookings table
create table bookings (
    booking_id int not null auto_increment primary key,
    guest_id int not null,
    event_id int not null,
    number_of_tickets int not null,
    total_price decimal(10, 2) not null,
    date_created datetime not null default current_timestamp, -- Added this line to store creation date and time
    foreign key (guest_id) references guests(guest_id),
    foreign key (event_id) references events(event_id)
);

-- payments table
create table payments (
    payment_id int not null auto_increment primary key,
    guest_id int not null,
    booking_id int not null,
    stripe_payment_id varchar(100) not null,
    amount decimal(10, 2) not null,
    currency varchar(10) not null,
    status varchar(20) not null,
    foreign key (guest_id) references guests(guest_id),
    foreign key (booking_id) references bookings(booking_id)
);

-- seats table
create table seats (
    seat_id int not null auto_increment primary key,
    guest_id int not null,
    booking_id int not null,
    event_id int not null,
    seat_number varchar(10) not null, -- Increased size of seat_number to varchar(10)
    foreign key (guest_id) references guests(guest_id),
    foreign key (booking_id) references bookings(booking_id),
    foreign key (event_id) references events(event_id)
);

-- reviews table
create table reviews (
    review_id int not null auto_increment primary key,
    event_id int not null,
    guest_id int not null,
    rating tinyint(1) not null,
    review_text text not null,
    review_timestamp datetime not null,
    foreign key (event_id) references events(event_id),
    foreign key (guest_id) references guests(guest_id)
);


